/etc/init.d/apache:
#!/bin/sh
#
# Copyright (c) 1999 by Sun Microsystems, Inc.
# All rights reserved.
#
#ident  &#x26;quot;@(#)apache.sh  1.3     99/11/10 SMI&#x26;quot;

APACHE_HOME=/opt/apache
CONF_FILE=/opt/apache/conf/httpd.conf
PIDFILE=/opt/apache/logs/httpd.pid

if [ ! -f ${CONF_FILE} ]; then
        exit 0
fi

START=$1

echo &#x26;quot;apache&#x26;quot;
case &#x26;quot;$1&#x26;quot; in
start)
        /bin/rm -f ${PIDFILE}
        cmdtext=&#x26;quot;starting&#x26;quot;
        START=startssl
        ;;
restart)
        cmdtext=&#x26;quot;restarting&#x26;quot;
        START=restart
        ;;
stop)
        cmdtext=&#x26;quot;stopping&#x26;quot;
        START=stop
        ;;
*)
        echo &#x26;quot;Usage: $0 {start|stop|restart}&#x26;quot;
        exit 1
        ;;
esac

echo &#x26;quot;httpd $cmdtext.&#x26;quot;

status=`${APACHE_HOME}/bin/apachectl $START 2&#x26;gt;&#x26;amp;1`

if [ $? != 0 ]; then
        echo &#x26;quot;$status&#x26;quot;
        exit 1
fi
exit 0

